INSERT INTO news (title, category, content, excerpt, image_url, date, featured, visible, read_time, views) VALUES
  ('راه‌اندازی خط تولید جدید شیشه‌های کریستالی', 'اخبار تولید', 'شرکت آترین پک با هدف افزایش ظرفیت تولید و بهبود کیفیت محصولات، خط تولید جدید شیشه‌های کریستالی را راه‌اندازی کرد. این خط تولید با استفاده از جدیدترین تکنولوژی‌های روز دنیا راه‌اندازی شده است.', 'آترین پک با راه‌اندازی خط تولید جدید، ظرفیت تولید شیشه‌های کریستالی را دو برابر کرده است.', 'https://images.unsplash.com/photo-1581092160562-40aa08e78837?w=400&h=300&fit=crop', '2024-01-15', true, true, '۳ دقیقه مطالعه', 245),
  ('دریافت گواهینامه ISO 9001:2015', 'گواهینامه‌ها', 'شرکت آترین پک موفق به دریافت گواهینامه بین‌المللی کیفیت ISO 9001:2015 شده است. این گواهینامه نشان‌دهنده تعهد شرکت به کیفیت و استانداردهای بین‌المللی است.', 'شرکت آترین پک موفق به دریافت گواهینامه بین‌المللی کیفیت ISO 9001:2015 شده است.', 'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=400&h=300&fit=crop', '2024-01-10', false, true, '۲ دقیقه مطالعه', 189),
  ('حضور در نمایشگاه بین‌المللی بسته‌بندی', 'نمایشگاه‌ها', 'آترین پک در نمایشگاه بین‌المللی بسته‌بندی تهران حضور یافت و محصولات جدید خود را معرفی کرد. این نمایشگاه فرصت مناسبی برای معرفی قابلیت‌های شرکت بود.', 'آترین پک در نمایشگاه بین‌المللی بسته‌بندی تهران حضور یافت و محصولات جدید خود را معرفی کرد.', 'https://images.unsplash.com/photo-1540575467063-178a50c2df87?w=400&h=300&fit=crop', '2024-01-05', false, true, '۴ دقیقه مطالعه', 156),
  ('معرفی سری جدید پمپ‌های اسپری طلایی', 'محصولات جدید', 'مجموعه جدید پمپ‌های اسپری با پوشش طلایی و کیفیت بالا به بازار عرضه شد. این محصولات با استفاده از بهترین مواد اولیه تولید شده‌اند.', 'مجموعه جدید پمپ‌های اسپری با پوشش طلایی و کیفیت بالا به بازار عرضه شد.', 'https://images.unsplash.com/photo-1563170351-be82bc888aa4?w=400&h=300&fit=crop', '2024-01-01', true, true, '۳ دقیقه مطالعه', 312),
  ('همکاری با برند معتبر فرانسوی', 'اخبار تولید', 'آترین پک قرارداد همکاری با یکی از برندهای معتبر فرانسوی در زمینه تولید بسته‌بندی لوکس امضا کرد. این همکاری فرصت‌های جدیدی را فراهم می‌کند.', 'آترین پک قرارداد همکاری با یکی از برندهای معتبر فرانسوی در زمینه تولید بسته‌بندی لوکس امضا کرد.', 'https://images.unsplash.com/photo-1556742049-0cfed4f6a45d?w=400&h=300&fit=crop', '2023-12-25', false, true, '۵ دقیقه مطالعه', 156);
